import styled from "styled-components";

const Row = styled.div`

    display: flex;
    justify-content: center;
    align-items: center;

`;

const Column = styled.div`

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

export { Row, Column };
