import ThemeInterface from "../../interfaces/theme/ThemeInterface";

const AppTheme: ThemeInterface = {
    softOrangeColor: "hsl(35, 77%, 62%)",
    softRedColor: "hsl(5, 85%, 63%)",
    offWhiteColor: "hsl(36, 100%, 99%)",
    greyishBlueColor: "hsl(233, 8%, 79%)",
    darkGreyishBlue: "hsl(236, 13%, 42%)",
    veryDarkBlueColor: "hsl(240, 100%, 5%)",
    bodyFontSize: "0.9375rem",
    FontWeightlight: 400,
    FontWeightMedium: 700,
    FontWeightHeavy: 800
};

export default AppTheme;
