import styled from "styled-components";
import { Row, Column } from "../../styles/layout/Flex";
import { images } from "../../constants/images";

const BottomSectionMainContainer = styled(Row)`

    width: 100%;

    height: 15%;

    background: #a7b087;

    align-items: flex-start;

    justify-content: flex-start;

    gap: 5%;

`;

const ArticleListTile = styled(Row)`

    height: 100%;
    width: 33.33%;
    align-items: flex-start;
    justify-content: flex-start;
    // gap: 0.75rem;
    background: lightblue;

`;

const ListTileImage = styled.img`

    height: 100%;
    width: 30%;
    // background-image: url(${images.imageWeb3Mobiile});
    // background-size: cover;

`;

const ListTileTop = styled.h3``;

const ListTileMiddle = styled.h3``;

const ListTileBottom = styled.p``;

const ListTileBodyContainer = styled(Column)`

align-items: flex-start;

justify-content: flex-start;

height: 100%;

width: 70%;

`;

export {
      BottomSectionMainContainer
    , ArticleListTile
    , ListTileImage
    , ListTileTop
    , ListTileMiddle
    , ListTileBottom
    , ListTileBodyContainer
}
